import os
CDIR = os.path.dirname(os.path.realpath(__file__))

class CRXSinfo:
    showinfo = 1
    def __init__(self):
        if CRXSinfo.showinfo>0:
            print(logo_MKastro)
            print(logo_CRXS)
            os.system('date >> CRXS_info.txt')
            os.system('echo "The Git SHA of the software is:" >> CRXS_info.txt')
            os.system('git --git-dir='+CDIR+'/../.git rev-parse HEAD    >> CRXS_info.txt')
            os.system('git --git-dir='+CDIR+'/../.git config branch.`git --git-dir='+CDIR+'/../.git name-rev --name-only HEAD`.remote | xargs git --git-dir='+CDIR+'/../.git remote get-url    >> CRXS_info.txt')
            os.system('echo " " >> CRXS_info.txt')
        CRXSinfo.showinfo = 0
